// rollup.config.js
import glob from "glob";
import path from "path";

const entryFiles = glob.sync("./*.js"); // Use your desired glob pattern here
console.log(entryFiles);
export default entryFiles
  .filter((f) => !f.includes("config") && !f.includes("webpack"))
  .map((file) => ({
    input: path.resolve(file),
    output: {
      dir: "src",
      format: "esm",
    },
    // Add other configurations and plugins as needed
  }));
