class Column {
    constructor({ name, type, nullable = true, length, defaultValue, min, max, alias, primary, unique, foreign, auto_increment, } = { defaultValue }) {
        this.columnConfig = {
            name,
            type,
            nullable,
            length,
            defaultValue,
            min,
            max,
            alias,
            primary,
            unique,
            foreign,
            auto_increment,
        };
    }
    get column() {
        if (this.columnConfig.alias) {
            return `${this.columnConfig.alias}.${this.columnConfig.name}`;
        }
        return this.columnConfig.name;
    }
    get type() {
        return this.columnConfig.type;
    }
    get alias() {
        return this.columnConfig.alias;
    }
}
module.exports = Column;
